import React, {Component} from 'react';
import Select from 'react-select';
import CustomFileInput from '../CustomFileInput';
import '../../Benefits/Benefits.css';
import { ToastContainer, toast } from 'react-toastify';
import {main_url} from "../../../utils/CommonFunction";

const requestByBranchOptions = [
    {value: 1, label: 'BM1'},
    {value: 2, label: 'BM2'},
    {value: 3, label: 'BM3'},
    {value: 4, label: 'BM4'},
];

class BirthdayFundAddNew extends Component {
    constructor() {
        super();
        this.state = {

            employeeName: [],
            user_id:'',

            requestByBranch: '',
            month: '',
            amount: ''
        }
    }

    componentDidMount() {
        fetch(`${main_url}benefit/getEmployeeList`)
            .then(res => { if (res.ok) return res.json() })
            .then(list => {
                this.setState({
                    employeeNameList: list
                })
            })
    }

    handleEmployeeName = (event) => {

        this.setState({
            user_id: event.value
        });
    };

    handleRequestByBranch = (event) => {
        this.setState({
            requestByBranch: event.label
        });
    };

    handleMonth = (event) => {
        this.setState({
            month: event.target.value
        });
    };

    handleAmount = (event) => {
        this.setState({
            amount: event.target.value
        });
    };


    // save = () => {
    //     let data = this.state.dataSource;
    //     data.push(this.state.funeralBenefitsData);

    //     this.setState({
